import { useState, useEffect } from "react";
import axios from "axios";

import { DynamicBarChart } from "react-dynamic-charts";
import "react-dynamic-charts/dist/index.css";

function App() {
  const [dataCovide, setDataCovide] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/covidworld")
      .then((response) => {
        let res = response.data;
        let date = "";
        let i = -1;
        res.map((val) => {
          if (date == val.timeline) {
            dataCovide[i].values.push({
              id: val.id_country,
              label: val.country,
              value: val.amount,
              color: val.color,
            });
            date = val.timeline;
          } else {
            dataCovide.push({
              name: val.timeline,
              values: [
                ...dataCovide,
                {
                  id: val.id_country,
                  label: val.country,
                  value: val.amount,
                  color: val.color,
                },
              ],
            });
            date = val.timeline;
            i++;
          }
        });
      })
      .then(() => {
        console.log("succsen");
      });
  }, []);

  return (
    <div>
      {
        <DynamicBarChart
          data={dataCovide}
          startAutomatically={false}
          showStartButton={true}
          iterationTimeout={1000}
          startButtonText={"Click Me!"}
        />
      }
    </div>
  );
}

export default App;
