import { useState, useEffect } from "react";
import axios from "axios";

import { DynamicBarChart } from "react-dynamic-charts";
import "react-dynamic-charts/dist/index.css";

function getRandomColor() {
  var letters = '0123456789ABCDEF';
  var color = '#';
  for (var i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

function App() {
  const [testData, setTestData] = useState([]);
  const [data, setData] = useState([]);
  const [add, setAdd] = useState([]);
  const [covid, setCovid] = useState([
    {
      "name": "Round 1",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 0,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 0,
          "color": ["yellow", "green"]
        }
      ]
    },
    {
      "name": "Round 2",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 10,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 5,
          "color": ["yellow", "green"]
        }
      ]
    },
    {
      "name": "Round 3",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 12,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 21,
          "color": ["yellow", "green"]
        }
      ]
    }
  ]);

  const testconsole = () => {
    console.log(data)
