import { useState, useEffect } from "react";
import axios from "axios";

import { DynamicBarChart } from "react-dynamic-charts";
import "react-dynamic-charts/dist/index.css";

function App() {
  const [dataCovide, setDataCovide] = useState([]);
  const [loading, setLoading] = useState(false);

  const renderElement = () =>{
    if(this.state.value == 'news')
       return <Text>data</Text>;
    return null;
 }

  useEffect(() => {
    axios
      .get("http://localhost:3001/covidworld")
      .then((response) => {
        let res = response.data;
        let date = "";
        let i = -1;
        res.map((val) => {
          if (date == val.timeline) {
            dataCovide[i].values.push({
              id: val.id_country,
              label: val.country,
              value: val.amount,
              color: val.color,
            });
            date = val.timeline;
          } else {
            dataCovide.push({
              name: val.timeline,
              values: [
                ...dataCovide,
                {
                  id: val.id_country,
                  label: val.country,
                  value: val.amount,
                  color: val.color,
                },
              ],
            });
            date = val.timeline;
            i++;
          }
        });
      })
      .then(() => {
        console.log("Complie")
        setLoading(true)
        renderElement
      });
  }, []);

  return (
    <div>
      {<DynamicBarChart data={dataCovide} iterationTimeout={1000}/>}
      {renderElement}
    </div>
  );
}

export default App;
