import { useState, useEffect } from "react";
import axios from "axios";

import { DynamicBarChart } from "react-dynamic-charts";
import "react-dynamic-charts/dist/index.css";

function App() {
  const [testData, setTestData] = useState([]);
  const [data, setData] = useState([]);
  const [add, setAdd] = useState([]);
  const [covid, setCovid] = useState([
    {
      name: "Round 1",
      values: [
        {
          id: 1,
          label: "Test 1",
          value: 0,
          color: "red",
        },
        {
          id: 2,
          label: "Test 2",
          value: 0,
          color: "yellow",
        },
        {
          id: 3,
          label: "Test 3",
          value: 0,
          color: "green",
        },
      ],
    },
  ]);

  const getRandomColor = () => {
    var letters = "0123456789ABCDEF";
    var color = "#";
    for (var i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  const testconsole = () => {
    data[0].map((items)=>{
      console.log(items.values);
    })
    // add.map((val)=>{
    // axios.post("http://localhost:3001/createcovidworld", {
    //     id_country: 0,
    //     country: val.country,
    //     province: val.province,
    //     timeline: '4/22/21',
    //     amount: val.timeline.cases['4/22/21']
    //   })
    //   .then(() => {}).catch((e)=>{console.log(e)})
    //   console.log(1)
    // })

    // update.map((val)=>{
    //   axios.put("http://localhost:3001/updatecovidworld", {
    //       id_country: val.id_country,
    //       country: val.country
    //     })
    //     .then(() => {}).catch((e)=>{console.log(e)})
    //     console.log(1)
    //   })
  };

  useEffect(() => {
    axios.get("http://localhost:3001/covidworld").then((response) => {
      // setTestData(response.data);
      let date = "";
      response.data.map((val) => {
        if (date == val.timeline) {
              val.values.push([{
              id: val.id_country,
              label: val.country,
              value: val.amount,
              color: "green",
            }])
          date = val.timeline;
        } else {
          data.push([
            {
              name: val.timeline,
              values: [
                {
                  id: val.id_country,
                  label: val.country,
                  value: val.amount,
                  color: "green",
                },
              ],
            },
          ]);
          date = val.timeline;
        }
      });
    });
    // axios.get("https://disease.sh/v3/covid-19/historical").then((response) => {
    //   setAdd(response.data);
    //   console.log('4/22/21')
    // });
  }, []);

  return (
    <div>
      <button onClick={testconsole}>Check</button>
      <button onClick={testconsole}>Check</button>
      {
        <DynamicBarChart
          data={data}
          startAutomatically={false}
          showStartButton={true}
          startButtonText={"Click Me!"}
        />
      }
    </div>
  );
}

export default App;
