import { useState, useEffect } from "react";
import axios from "axios";

import { DynamicBarChart } from "react-dynamic-charts";
import "react-dynamic-charts/dist/index.css";

function getRandomColor() {
  var letters = '0123456789ABCDEF';
  var color = '#';
  for (var i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

function App() {
  const [testData, setTestData] = useState([]);
  const [dataCovide, setDataCovide] = useState([]);
  const [updateColorC, setUpdateColorC] = useState([
    {
      country: ''
    },
  ]);
  const [updateColorP, setUpdateColorP] = useState([]);
  const [covid, setCovid] = useState([
    {
      "name": "Round 1",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 0,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 0,
          "color": ["yellow", "green"]
        }
      ]
    },
    {
      "name": "Round 2",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 10,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 5,
          "color": ["yellow", "green"]
        }
      ]
    },
    {
      "name": "Round 3",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 12,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 21,
          "color": ["yellow", "green"]
        }
      ]
    }
  ]);

  const testconsole = () => {
    let colors = getRandomColor();
    console.log(colors)
    // dataCovide[1].map((items,i)=>{
    //   console.log(items.values[0].id);
    // })
    // add.map((val)=>{
    // axios.post("http://localhost:3001/createcovidworld", {
    //     id_country: 0,
    //     country: val.country,
    //     province: val.province,
    //     timeline: '4/22/21',
    //     amount: val.timeline.cases['4/22/21']
    //   })
    //   .then(() => {}).catch((e)=>{console.log(e)})
    //   console.log(1)
    // })

    // update.map((val)=>{
    //   axios.put("http://localhost:3001/updatecovidworld", {
    //       id_country: val.id_country,
    //       country: val.country
    //     })
    //     .then(() => {}).catch((e)=>{console.log(e)})
    //     console.log(1)
    //   })
  };

  const UpdateColorCountry = () =>{
    updateColorC.map((val)=>{
      let colors = getRandomColor();
      axios.put("http://localhost:3001/updatecovidworld/c", {
          color: colors,
          country: val.country
        })
        .then(() => {}).catch((e)=>{console.log(e)})
        console.log(1)
      })
  }

  const UpdateColorProvince = () =>{
    updateColorP.map((val)=>{
      let colorss = getRandomColor();
      axios.put("http://localhost:3001/updatecovidworld/p", {
          color: colorss,
          province: val.province
        })
        .then(() => {}).catch((e)=>{console.log(e)})
        console.log(1)
      })
  }

  useEffect(() => {
    axios.get("http://localhost:3001/covidworld").then((response) => {
      // setTestData(response.data);
      let res = response.data
      let date = "";
      let i = -1 ; 
      res.map((val)=> {
      let color = getRandomColor();
        if (date == val.timeline) {
          dataCovide[i].values.push({
                    id: val.id_country,
                    label: val.country,
                    value: val.amount,
                    color: color
          })
          // dataCovide.map((res)=>{
          //   res.values.push({
          //       id: val.id_country,
          //       label: val.country,
          //       value: val.amount,
          //       color: color,
          //   })
          // })
          date = val.timeline;
        } else {
          dataCovide.push({
              name: val.timeline,
              values: [...dataCovide,
                {
                  id: val.id_country,
                  label: val.country,
                  value: val.amount,
                  color: color,
                },
              ],
            });
          date = val.timeline;
          i++
        }
      });
    }).then(()=>{
      console.log("succsen")
    });
    // axios.get("https://disease.sh/v3/covid-19/historical").then((response) => {
    //   setAdd(response.data);
    //   console.log('4/22/21')
    // });
  }, []);

  return (
    <div>
      <button onClick={testconsole}>Check</button>
      <button onClick={testconsole}>Check</button>
      {
        <DynamicBarChart
          data={dataCovide}
          startAutomatically={false}
          showStartButton={true}
          iterationTimeout={1000}
          startButtonText={"Click Me!"}
        />
      }
    </div>
  );
}

export default App;
