import { useState, useEffect } from 'react';
import axios from 'axios';

import { DynamicBarChart } from 'react-dynamic-charts';
import 'react-dynamic-charts/dist/index.css'; // Don't forget to import the styles

function App() {
  const [testData, setTestData] = useState([])
  const [data, setData] = useState([
    {
      "name": "Round 1",
      "values": [
        {
          "id": 1,
          "label": "Test 1",
          "value": 0,
          "color": "red"
        },
        {
          "id": 2,
          "label": "Test 2",
          "value": 0,
          "color": "yellow"
        },
        {
          "id": 3,
          "label": "Test 3",
          "value": 0,
          "color": "green"
        }

      ]
    },
  ]);

  const testconsole = () => {
    let time = "3/23/21"
    testData.map((val)=>{
      axios
      .post("http://localhost:3001/createcovidworld", {
        country: val.country,
        timeline: "3/23/21",
        amount: val.timeline.cases["3/23/21"],
      })
      .then(() => {
        console.log("success")
      }).catch((e)=>{console.log(e)})
    })
  }

  useEffect(() => {
    axios.get("https://disease.sh/v3/covid-19/historical").then((response) => {
      setTestData(response.data);
      console.log(response.data)
        // let date = "";
        // response.data.map((val,key)=>{
        //   if(date == val.age){
        //     data.value.map((value,key)=>{
        //       setData([...data.values, {name:value.name}])
        //     })
        //   date = val.age;
        // }else{
        //   setData([...data, { age: val.age, value:[ {name:val.name}]}])
        //   date = val.age;
        //   }
        // })
      });
  }, [])

    return (
      <div>
        <button onClick={testconsole}>Check</button>
        {
      <DynamicBarChart
        data={data}
        iterationTimeout={1000}
      />
        }
      </div>
    )
};

export default App;
